public class Maths {
	

	public double add(double k, double l) {      //Function where the process of addition is defined
		// TODO Auto-generated method stub
		return k+l;
	}

	public double subtract(double k, double l) {		//Function where the process of Subtraction is defined
		// TODO Auto-generated method stub
		
		return k-l;
		
	}
	public double div(double k, double l) {				//Function where the process of Division is defined
		// TODO Auto-generated method stub
		
		return k/l;
		
	}
	public double mul(double k, double l) {				//Function where the process of Multiplication is defined
		// TODO Auto-generated method stub
		
		return k*l;
	}
	
	
	

}



